        public void LoadLanguageCore()
        {
            //从JSON字符串生成集合对象用于存放语言包列表
            string json = "[{\"LangID\":\"0001\",\"LangVersion\":\"1.0.0.4\",\"LangName\":\"简体中文\",\"LangFileName\":\"Lang_CN\"},{\"LangID\":\"0002\",\"LangVersion\":\"1.0.0.4\",\"LangName\":\"繁体中文\",\"LangFileName\":\"Lang_TW\"},{\"LangID\":\"0003\",\"LangVersion\":\"1.0.0.4\",\"LangName\":\"English\",\"LangFileName\":\"Lang_EN\"}]";
            App.Current.LangList = JsonConvert.DeserializeObject<List<LanguageListModel>>(json);
            string langjson;
            switch (App.Current.IniModel.ReadLang)
            {
                case "Lang_CN":
                    langjson = Lang_CN;
                    break;
                case "Lang_TW":
                    langjson = Lang_TW;
                    break;
                case "Lang_EN":
                    langjson = Lang_EN;
                    break;
                default:
                    langjson = Lang_TW;
                    break;
            }
            App.Current.Language = JsonConvert.DeserializeObject<LanguageModel>(langjson);
        }
    }
